import React, {useState, createRef} from 'react';
import {
  StyleSheet,
  TextInput,
  View,  
  ScrollView,
  Image,
  Keyboard,
  ImageBackground,
  TouchableOpacity,
  KeyboardAvoidingView,
  Dimensions
} from 'react-native';
import { Block, Checkbox, Text, Button, theme, Input, Icon } from "galio-framework";
import AsyncStorage from '@react-native-async-storage/async-storage';
import { begguTheme } from "../../constants"
import Loader from '../components/loader';
import { useSelector, useDispatch } from 'react-redux'
import {clientesActions} from '../../actions'
import { EvilIcons, FontAwesome5 } from '@expo/vector-icons'

const { width, height } = Dimensions.get("screen");

const _retrieveData = async (key) => {
  try {
    const data = await AsyncStorage.getItem(key);
    return data;
  } catch (error) {
    console.log(error);
  }
}

const LoginScreen = ({navigation}) => {
  const [userUsername, setUserUsername] = useState('');
  const [userPassword, setUserPassword] = useState('');  
  const [errortext, setErrortext] = useState('');

  const loading = useSelector(state => state.clientes.loading)  
  const user = useSelector(state => state.clientes.user)  
  const isRegistraionSuccess = useSelector(state => state.clientes.isRegistraionSuccess)


  const dispatch = useDispatch()

  const handleSubmitButton = () => {
    setErrortext('');
    if (!userPassword) {
      alert('Por favor ingrese password');
      return;
    }    
    if (!userUsername) {
      alert('Por favor ingrese username');
      return;
    }

    var dataToSend = {      
      username: userUsername,
      password: userPassword 
    };
    dispatch(clientesActions.login(dataToSend))     

    _retrieveData('userId').then((userId)=>{      
      if(userId !=='0'){
        navigation.navigate('Home');        
      }else{        
        alert('No existe usuario')
      }      
    });

  } 
  return (    
    <Block flex style={{backgroundColor: '#F4F5F7'}}>
    <Loader loading={loading} />
    <Block flex style={styles.group}>
    <ScrollView
        keyboardShouldPersistTaps="handled"
        contentContainerStyle={{
          justifyContent: 'center',
          alignContent: 'center',
          marginTop: 10
        }}>

      <KeyboardAvoidingView enabled>      
      <Block style={{ paddingHorizontal: theme.SIZES.BASE }}>
        <Input
            placeholder="Nombres"
            placeholderTextColor={begguTheme.COLORS.MUTED}
            style={styles.inputStyle}
            color={begguTheme.COLORS.HEADER}    
            onChangeText={(Username) => setUserUsername(Username)}
            iconContent={
              <Icon
                size={20}
                color={begguTheme.COLORS.ICON}
                name="user"
                family="Entypo"
                style={styles.icons}
              />
            }  

        />
      </Block>
      <Block style={{ paddingHorizontal: theme.SIZES.BASE }}>
        <Input
            placeholder="Password"
            placeholderTextColor={begguTheme.COLORS.MUTED}
            style={styles.inputStyle}
            color={begguTheme.COLORS.HEADER}    
            secureTextEntry={true}
            onChangeText={(Password) => setUserPassword(Password)}
            iconContent={
              <Icon
                size={18}
                color={begguTheme.COLORS.ICON}
                name="key"
                family="Entypo"
                style={styles.icons}
              />
            }  

        />
      </Block>

      <Block style={{ paddingHorizontal: theme.SIZES.BASE }}>
          <TouchableOpacity
            style={styles.buttonStyle}
            activeOpacity={0.5}
            onPress={handleSubmitButton}>
            <Text style={styles.buttonTextStyle}>
              LOGIN
            </Text>
          </TouchableOpacity>
      </Block>
      </KeyboardAvoidingView>  

      <Block style={styles.registro}>
        <TouchableOpacity  onPress={() => navigation.navigate('Register')} style={styles.imgSearch}>                        
            <Text style={styles.txtLink}>Registrate</Text>
        </TouchableOpacity>  
        <TouchableOpacity  onPress={() => navigation.navigate('Recuperate')} style={styles.imgSearch}>                        
            <Text style={styles.txtLink}>Olvidaste tu contrase√±a ?</Text>
        </TouchableOpacity>  
      </Block>
    </ScrollView>      
    </Block> 

  </Block>   
  );
};
export default LoginScreen;

const styles = StyleSheet.create({
  mainBody: {
    flex: 1,
    justifyContent: 'center',
    backgroundColor: '#307ecc',
    alignContent: 'center',
  },
  SectionStyle: {
    flexDirection: 'row',
    height: 40,
    marginTop: 20,
    marginLeft: 35,
    marginRight: 35,
    margin: 10,
  },
  buttonStyle: {
    backgroundColor: '#7DE24E',
    borderWidth: 0,
    color: '#FFFFFF',
    borderColor: '#7DE24E',
    height: 40,
    alignItems: 'center',
    borderRadius: 8,
    marginLeft: 35,
    marginRight: 35,
    marginTop: 20,
    marginBottom: 25,
  },
  buttonTextStyle: {
    color: '#FFFFFF',
    paddingVertical: 10,
    fontSize: 16,
  },
  inputStyle: {
    flex:1,
    width: '100%',
    borderRadius: 8,
    borderColor: begguTheme.COLORS.BORDER,    
    backgroundColor: '#FFFFFF',
    paddingLeft: 15
  },
  registerTextStyle: {
    color: '#FFFFFF',
    textAlign: 'center',
    fontWeight: 'bold',
    fontSize: 14,
    alignSelf: 'center',
    padding: 10,
  },
  errorTextStyle: {
    color: 'red',
    textAlign: 'center',
    fontSize: 14,
  },
  group:{
    borderWidth:2,
    borderColor: '#fff',
    padding:10
  },
  icons: {    
    paddingLeft: 2,
    paddingRight: 15
  },
  registro:{                
    alignSelf: 'center',
    width: '75%',
    textAlign: 'center',
    alignItems:'center'
  },
  imgSearch:{
    flexDirection:'row'
  },
  txtSearch:{
    paddingLeft:5,
    paddingTop: 6,
    fontSize: 14,
    color:"#c1c1c1"
  },
  txtLink:{
    color:"#0b86f6",
    paddingRight: 8,
    marginTop: 5
  }

});
