const initialState = {
    data:[],
    pagina:0,
    paginas:0,
    sucursales:[],
    item:{},
    categorias:[],
    servicios:[],
    emergencias:[],
    cajeros:[],
    comidas:[],
    user:{
      id:'',
      nombre:'',
      telefono:'',
      email:'',
      username:'',
      password:'',
      refusername:'',
      refemail:'',
      refnombre:''
    },
    modalView: false,
    loading: false,
    isRegistraionSuccess: false
}

export function clientes(state = initialState, action){
    switch(action.type){
      case "USER_ITEM":
        return {
            ...state,                
            user: action.user
        }
      case "LOGIN_SUCCESS":
        return {
            ...state,                
            user: action.user ? action.user : initialState.user
        }
        case "GET_DATA":
            return {
                ...state,                
                data: action.payload.pagina  === 1 ? action.payload.data : [ ...state.data, ...action.payload.data],
                pagina:action.payload.pagina,
                paginas:action.payload.paginas                

            }        
        case "GET_ITEM":
                return {
                    ...state,
                    item: action.payload,
                    sucursales: action.payloads
                }
        case "GET_CATEGORIAS":
                return {
                ...state,
                categorias: action.payload
                } 
        case "GET_EMERGENCIAS":
                return {
                ...state,
                emergencias: action.payload
                }
        case "GET_COMIDAS":
                  return {
                  ...state,
                  comidas: action.payload
                  }         
        case "GET_SERVICIOS":
                return {
                ...state,
                servicios: action.payload
                }
        case "GET_CAJEROS":
                  return {
                  ...state,
                  cajeros: action.payload
                }
        case "RESET_CAJEROS":
               return {
                ...state,
                cajeros: []
              }
        case "RESET_CLIENTES":
                return {
                 ...state,
                 data: []
               }      
        case "RESET_EMERGENCIAS":
                return {
                 ...state,
                 emergencias: []
               }
        case "RESET_SERVICIOS":
                return {
                 ...state,
                 servicios: []
               }
        case "RESET_USER":
                return {
                 ...state,
                 user: initialState.user
               }        
        case "RESET_COMIDAS":
                return {
                 ...state,
                 comidas: []
               }                             
        case "START_MODAL":
              return {
                ...state,
              modalView: true
            }
        case "END_MODAL":
                return {
                  ...state,
                modalView: false
              }
        case "SET_REGISTRO":
                return {
                  ...state,          
                  isRegistraionSuccess: true      
              }              
        case "START_LOAD":
                return {
                  ...state,
                loading: true
              }
        case "STOP_LOAD":
                return {
                  ...state,
                loading: false
              }            
        default:
            return state
    }
}
