import { StatusBar } from 'expo-status-bar';
import React, {useEffect, useState} from 'react';
import { Image, ImageBackground, FlatList, ScrollView, Dimensions, StyleSheet, View, Linking } from 'react-native';
import { useSelector, useDispatch } from 'react-redux'
import { api } from '../../helpers'
import { WhatsAppIcon, PhoneIcon, MapsIcon, FacebookIcon, InstagramIcon } from '../../components/Icons'
import { Images, begguTheme } from "../../constants";
import { HeaderHeight } from "../../constants/utils";
import { Block, Text, theme } from "galio-framework";
import { FontAwesome } from '@expo/vector-icons'
import Banner from './Marketing/Banner'
import Slider from './Marketing/Slider'
import Video from './Marketing/Video'

const { height, width } = Dimensions.get("screen")
const thumbMeasure = (width - 48 - 32) / 3;

function separator (){
  return <View style={{ borderBottomWidth: 0.1, borderBottomColor:'"#eaeaea' }}/>
}

function DetailScreen ({ route, navigation }){      
  const cliente = useSelector(state => state.clientes.item) 
  const sucursales = useSelector(state => state.clientes.sucursales)    
  return(
    <Block flex style={styles.profile}>      
     <ImageBackground
            source={Images.ProfileBackground}
            style={styles.profileContainer}
            imageStyle={styles.profileBackground}
      >
        <Block style={styles.profileCard} >
          <Block middle style={styles.avatarContainer}>
            <Image source={{uri:`${api}/static/images/clientes/sm/${cliente.filename}`}} style={styles.avatar} />
          </Block>

          <Block middle style={styles.nameInfo}>
              <Text bold size={21} color="#32325D" numberOfC numberOfLines={1}>{cliente.nombres}</Text>
              <Text color="#32325D" style={styles.textDescripcion} numberOfLines={1}>{cliente.direccion}</Text>
              <Text color="#32325D" style={styles.textDescripcion} numberOfLines={1}>{cliente.telefono}</Text>
              <Text color="#015eea" style={styles.textDescripcion} 
                onPress={() => { Linking.openURL(`https://${cliente.web}`);   }}> {cliente.web}</Text>
              <Text color="#32325D" style={styles.textEmail}>{cliente.email}</Text>                
          </Block>

          <Block style={styles.info}>
            <Block  middle row space="evenly" style={{ marginTop: 5, paddingBottom: 1 }}>
                <Block middle style={styles.icono}>
                  <FacebookIcon enlace={cliente.facebook}/>
                </Block>
                <Block middle style={styles.icono}>
                  <InstagramIcon enlace={cliente.instagram}/>
                </Block>

                <Block middle style={styles.icono}>
                  <PhoneIcon telefono={cliente.telefono} />
                </Block>

                <Block middle style={styles.icono}>
                  <WhatsAppIcon telefono={cliente.celular} mesage={'Buenas tardes.... '}    />
                </Block>

                <Block middle style={styles.icono}>                  
                  <MapsIcon
                    latitude={cliente.latitude}
                    longitude={cliente.longitude}
                    title={cliente.nombres}                      
                    telefono={cliente.telefono}               
                  />
                </Block>  
            </Block>
          </Block>
        </Block>
        <Block style={styles.profileMarketing} >
          { MarketinComponent(cliente) }                                    
        </Block>

        <Block middle style={styles.profileSucursal}>   
            <Text bold size={18} color="#4d4d4d">Sucursales</Text>
            <FlatList
            data={sucursales}                 
            keyExtractor={item => item.id.toString()}
            renderItem={({ item }) =>  

              <Block card style={styles.card}>   
              <View style={styles.inicial}>                                       
                <View style={styles.itemTexto}>                
                  <Text style={styles.textTitulo}>{item.nombre}</Text>                        
                  <Text style={styles.textDescripcion} numberOfLines={1}>{item.direccion}</Text>
                  <Text style={styles.textDescripcion}>Teléfono: {item.telefono}</Text>  
                  <Text style={styles.textEstado}>{item.Horarios.hinicio} - {item.Horarios.hfin}</Text>                                                           
                </View>
                <FontAwesome 
                    style={styles.hora} 
                    name="circle" 
                    size={18} 
                    color={(item.Horarios.hinicio === '00:00' && item.Horarios.hfin === '00:00')? 'red': 'green'}
                />     
              </View>  
              <View style={styles.secundario}>
                  <Block middle style={styles.icono}>
                        <PhoneIcon telefono={item.telefono} />
                  </Block>
                  <Block middle style={styles.icono}>  
                    <MapsIcon
                      latitude={item.latitude}
                      longitude={item.longitude}
                      title={item.nombre}                      
                      telefono={item.telefono}                  
                    />
                  </Block>
              </View>
              </Block>                     

            }                     
          />
        </Block>    
      </ImageBackground>  
    </Block>

  ) 
}

function MarketinComponent(cliente){  
  switch(cliente.paqueteId){
    case '1':
      return <View><Text size={17} style={styles.textoMarketing}>Sin Publicidad</Text></View>
      break
    case '2':
      return <Banner banner={cliente.banner}/>
      break  
    case '3':
      return <Slider img1={cliente.slider1} img2={cliente.slider2} img3={cliente.slider3}/>
      break    
    case '4':
      return <Video video={cliente.video} />
      break    
  }

}

const styles = StyleSheet.create({  
  profile: {
    marginTop: Platform.OS === "android" ? -HeaderHeight : 0,
    // marginBottom: -HeaderHeight * 2,
    flex: 1
  },
  profileContainer: {
    width: width,
    height: height,
    padding: 0,
    zIndex: 1,

  },
  profileCard: {
    // position: "relative",
    padding: theme.SIZES.BASE,
    marginHorizontal: theme.SIZES.BASE,
    marginTop: 40,
    borderTopLeftRadius: 6,
    borderTopRightRadius: 6,
    borderBottomLeftRadius: 6,
    borderBottomRightRadius: 6,
    backgroundColor: theme.COLORS.WHITE,
    shadowColor: "black",
    shadowOffset: { width: 0, height: 0 },
    shadowRadius: 8,
    shadowOpacity: 0.2,
    zIndex: 2,
    height: 190

  },
  profileSucursal: {
    // position: "relative",        
    marginHorizontal: theme.SIZES.BASE,
    marginTop: 2,    
    borderTopLeftRadius: 6,
    borderTopRightRadius: 6,
    borderBottomLeftRadius: 6,
    borderBottomRightRadius: 6,
    backgroundColor: theme.COLORS.WHITE,    
    height: height /2.15
  },
  profileMarketing: {    
    backgroundColor:'#4d4d4d',    
    marginHorizontal: theme.SIZES.BASE,
    padding:2,    
    alignContent:'center',
    height: 115
    },

  nameInfo: {    
    alignItems:'flex-end',
    width: "75%",
    marginTop: -40,
    marginLeft: 75
  },
  info: {
    paddingHorizontal: 40,        
    marginTop: 5    
  },

  textoMarketing: {
    // position: "relative",            

    textAlign:'center',
    padding:20,
    color: '#eaeaea' 
  },
  avatarContainer: {
    position: "relative",
    marginTop: -35,
    marginStart: -25,
    alignItems:'flex-start'
  },
  avatar: {
    width: 104,
    height: 104,
    borderRadius: 62,
    borderWidth: 2,
    borderColor: "#c1c1c1"
  },
  mtkContainer: {
    //position: "relative"
    height: 104,
    borderWidth: 2,
    borderColor: "#c1c1c1"
  },
  mtk: {
    width: '100%',
    height: 70,    
    borderWidth: 2,
    borderColor: "#c1c1c1"
  },

  icono: {
    borderColor: "#c1c1c1",    
    padding:2,
    borderWidth:1,
    borderRadius: 25
  },

  card: {
    backgroundColor: theme.COLORS.WHITE,
    marginVertical: theme.SIZES.BASE,
    borderWidth: 0,
    minHeight: 77,
    marginBottom: 0,
    marginTop: 1.4,
    borderWidth:1,
    borderColor: '#c1c1c1'
  },
  inicial:{
    flexDirection: 'row'
  },
  secundario:{
    position:'relative',    
    flexDirection: 'row',
    alignItems:'flex-end',
    paddingLeft: 260,
    marginTop: -22
  },
  itemTexto: {    
    width: '93%',
    borderRadius: 4,
    paddingLeft:7,        
    marginTop:1,
    marginBottom:1
  },
  itemLinks: {    
    width: '20%',
    borderRadius: 4,
    paddingLeft:7,        
    marginTop:1,
    marginBottom:1,    
  },
  textTitulo:{
    fontSize:14.5,    
    fontWeight: '700',    
    marginBottom:1
  },  
  textDescripcion:{
     fontSize:11,     
  },
  textEmail:{
    fontSize:11,     
    color: '#015eea'
 },
  textEstado:{
    fontSize:11,     
    fontWeight:"700"
 },
 hora:{

  position: 'relative',  
  marginTop: 2
}, 

});
export default DetailScreen
