import React, {useState, createRef, useEffect} from 'react';
import {
  StyleSheet,
  TextInput,
  View,  
  Image,
  KeyboardAvoidingView,
  Keyboard,
  TouchableOpacity,
  ScrollView,
  Dimensions,
  Button
} from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { Block, Text, theme } from "galio-framework";
import ClientesLista from '../Home/ClientesLista'
import Categorias from './Categorias'
import { PanicIcon } from '../../components/Icons'

import { useSelector, useDispatch } from 'react-redux'
import {clientesActions} from '../../actions'
const { width } = Dimensions.get('screen');

const HomeScreen = ({navigation}) => {  
  const [loading, setLoading] = useState(false);    
  const dispatch = useDispatch()

  useEffect(() =>{    
    let mounted = true;    
    if(mounted){
      dispatch(clientesActions.getData(1,10,0,true,0))
      dispatch(clientesActions.getCategorias()) 
    }
    return () =>{      
      dispatch(clientesActions.resetL('RESET_CLIENTES'))      
    };
  }, []);



  return (
    <Block flex center style={styles.home}> 
      <Block style={styles.categoriaContainer}>
        <Block style={styles.categorias}>        
          <Categorias/>          
        </Block>
        <Block style={styles.panico}>
          <Block flex middle right>
              <PanicIcon/>
          </Block>
        </Block>
      </Block>

      <ClientesLista navigation={navigation} />
    </Block>
  );
};
export default HomeScreen;

const styles = StyleSheet.create({
  home:{
    width: width,
  },
  categoriaContainer:{        
    flexDirection:'row',
    alignItems:'center',        
    padding:2,
    backgroundColor: '#4d4d4d'
  },
  categorias:{
    borderColor: '#636363',
    borderWidth: 2,    
    width: '80%',
    height: 50
  },
  panico:{     
    width: '20%',
    height: 50,
    alignItems:'center'
  },
  social: {
    width: theme.SIZES.BASE * 3,
    height: theme.SIZES.BASE * 3,
    borderRadius: theme.SIZES.BASE * 1.75,
    justifyContent: "center",    
  },
  shadow: {
    shadowColor: "black",
    shadowOffset: { width: 0, height: 2 },
    shadowRadius: 4,
    shadowOpacity: 0.2,
    elevation: 2
  },
});
