import { StatusBar } from 'expo-status-bar';
import React, {useEffect, useState} from 'react';
import { Image, ImageBackground, FlatList, ScrollView, Dimensions, StyleSheet, View, Linking, TouchableOpacity } from 'react-native';
import { useSelector, useDispatch } from 'react-redux'
import { api } from '../../helpers'
import { WhatsAppIcon, PhoneIcon, MapsIcon, FacebookIcon, InstagramIcon } from '../../components/Icons'
import { Images, begguTheme } from "../../constants";
import { HeaderHeight } from "../../constants/utils";
import { Block, Text, theme } from "galio-framework";
import { EvilIcons, AntDesign } from '@expo/vector-icons'
import AsyncStorage from '@react-native-async-storage/async-storage';
import {clientesActions} from '../../actions'

const { height, width } = Dimensions.get("screen")
const thumbMeasure = (width - 48 - 32) / 3;

function PerfilScreen ({ route, navigation }){                  
  const user = useSelector(state => state.clientes.user)   
  const dispatch = useDispatch()

  /*function getSalir () {            
    AsyncStorage.setItem('userId', null) 
    navigation.navigate('Home')
  }*/

  useEffect(() =>{    
    let mounted = true;        
    return () =>{      
      dispatch(clientesActions.resetL('RESET_USER'))      
    };
  }, []);


  const _retrieveData = async (key) => {
    try {
      await AsyncStorage.setItem('userId', '0'); 
      navigation.navigate('Home')
      return true;
    } catch (error) {
      console.log(error);
    }
  } 

          return (
            <Block flex style={styles.profile}>
              <Block flex>
                <ImageBackground
                  source={Images.ProfileBackground}
                  style={styles.profileContainer}
                  imageStyle={styles.profileBackground}
                >
                  <ScrollView
                    showsVerticalScrollIndicator={false}
                    style={{ width, marginTop: '25%' }}
                  >
                    <Block flex style={styles.profileCard}>
                      <Block middle style={styles.avatarContainer}>
                      <EvilIcons name="user" size={85} color="#eaeaea" />
                      </Block>                      
                      <Block flex>
                        <Block middle style={styles.nameInfo}>
                          <Text bold size={28} color="#32325D">
                            { user.nombre }
                          </Text>
                          <Text size={16} color="#32325D" style={{ marginTop: 5 }}>
                            {user.telefono}
                          </Text>
                          <Text size={16} color="#32325D" style={{ marginTop: 5 }}>
                            {user.email}
                          </Text>
                        </Block>
                        <Block middle style={{ marginTop: 10, marginBottom: 5 }}>
                          <Block style={styles.divider} />
                        </Block>            

                        <Block style={{ paddingVertical: 14  }}                    >
                          <Text bold size={16} color="#525F7F">
                          Nombre referencia  
                          </Text>
                          <Text size={16} color="#525F7F">
                          { user.refnombre }
                          </Text>
                        </Block>
                        <Block style={{ paddingVertical: 14 }}                    >
                          <Text bold size={16} color="#525F7F">
                          E-mail de referencia  
                          </Text>
                          <Text size={16} color="#525F7F">
                          { user.refemail }
                          </Text>
                        </Block>
                        <Block style={styles.registro}>
                          <TouchableOpacity  onPress={() => _retrieveData(null) } style={styles.imgSearch}>                        
                              <AntDesign name="logout" size={24} color="red" />      
                              <Text>Salir</Text>                        
                          </TouchableOpacity>                            
                        </Block>                        
                      </Block>
                    </Block>
                  </ScrollView>
                </ImageBackground>
              </Block>
              {}
            </Block>
          );
        }


const styles = StyleSheet.create({
  profile: {
    marginTop: Platform.OS === "android" ? -HeaderHeight : 0,
    // marginBottom: -HeaderHeight * 2,
    flex: 1
  },
  profileContainer: {
    width: width,
    height: height,
    padding: 0,
    zIndex: 1
  },
  profileBackground: {
    width: width,
    height: height / 2
  },
  profileCard: {
    // position: "relative",
    padding: theme.SIZES.BASE,
    marginHorizontal: theme.SIZES.BASE,
    marginTop: 65,
    borderTopLeftRadius: 6,
    borderTopRightRadius: 6,
    backgroundColor: theme.COLORS.WHITE,
    shadowColor: "black",
    shadowOffset: { width: 0, height: 0 },
    shadowRadius: 8,
    shadowOpacity: 0.2,
    zIndex: 2
  },
  info: {
    paddingHorizontal: 40
  },
  avatarContainer: {
    position: "relative",
    marginTop: -100,        
    height: 100,    
  },
  avatar: {
    width: 124,
    height: 124,
    borderRadius: 62,
    borderWidth: 0
  },
  nameInfo: {
    marginTop: 35
  },
  divider: {
    width: "90%",
    borderWidth: 1,
    borderColor: "#E9ECEF"
  },
  thumb: {
    borderRadius: 4,
    marginVertical: 4,
    alignSelf: "center",
    width: thumbMeasure,
    height: thumbMeasure
  },
  registro:{
    alignSelf:'flex-end'

  }
});
export default PerfilScreen
