import {clientesApi} from '../api'

export const clientesActions = {
    getData,
    getDatas,
    getItem,
    getCategorias,
    getServicios,
    getEmergencias,
    getCajeros,
    getComidas,
    registro,
    login,
    sendPanico,
    getUsuario,
    resetL
  };

  function getUsuario(userId) {
    return (dispatch) => {
      /*dispatch(mStart())*/
      clientesApi
        .getUsuario(userId)
        .then((response) => {                    
          dispatch(ITEM_USR(response.result));                    
          /*dispatch(mStop())*/
        })
        .catch((err) => {
          /*dispatch(createNotification(alertActions.error(err)));
          dispatch(final());*/
        });
    };
  }

  export function ITEM_USR(user) {
    return {
      type: "USER_ITEM",
      user: user
    };
  }


  function sendPanico(user) {
    return (dispatch) => {
      dispatch(mStart())
      clientesApi
        .sendPanico(user)
        .then((response) => {          
          console.log(response)
          /*dispatch(
            createNotification(alertActions.success(response.cliente.message))
          );
          history.push("/admin");*/
          dispatch(mStop())
        })
        .catch((err) => {
          /*dispatch(createNotification(alertActions.error(err)));
          dispatch(final());*/
        });
    };
  }


  function login(user) {
    return (dispatch) => {
      dispatch(mStart())
      clientesApi
        .login(user)
        .then((response) => {          
          dispatch(LOGIN(response.result.user));                    
          dispatch(mStop())
        })
        .catch((err) => {          
          dispatch(mStop());
        });
    };
  }

  export function LOGIN(user) {
    return {
      type: "LOGIN_SUCCESS",
      user: user
    };
  }

  function registro(data) {
    return (dispatch) => { 
      dispatch(mStart())
      clientesApi
      .registro(data)
      .then((response)=>{                                   
        dispatch(pRegistro(response.message))               
        dispatch(mStop())
      })
      .catch((err)=>{
        console.log(err)
      })
    };
  }

  export function pRegistro(response) {
    //response.message
    //response.token
    //response.user.
    return {
      type: 'SET_REGISTRO',
      payload: response
    };
  }

  function getServicios() {
    return (dispatch) => {    
      dispatch(mBegin())        
      clientesApi
      .getServicios()
      .then((response)=>{                   
        dispatch(pServicios(response.result))       
        dispatch(mEnd())
      })
      .catch((err)=>{
        console.log(err)
      })
    };
  }

  export function pServicios(response) {
    return {
      type: 'GET_SERVICIOS',
      payload: response
    };
  }


  function getEmergencias() {
    return (dispatch) => {   
      dispatch(mStart())         
      clientesApi
      .getEmergencias()
      .then((response)=>{                   
        dispatch(pEmergencias(response.result))       
        dispatch(mStop())
      })
      .catch((err)=>{
        console.log(err)
      })
    };
  }

  export function pEmergencias(response) {
    return {
      type: 'GET_EMERGENCIAS',
      payload: response
    };
  }

  function getComidas() {
    return (dispatch) => {   
      dispatch(mStart())         
      clientesApi
      .getComidas()
      .then((response)=>{                   
        dispatch(pComidas(response.result))       
        dispatch(mStop())
      })
      .catch((err)=>{
        console.log(err)
      })
    };
  }

  export function pComidas(response) {
    return {
      type: 'GET_COMIDAS',
      payload: response
    };
  }

  function getCajeros() {
    return (dispatch) => {   
      dispatch(mStart())         
      clientesApi
      .getCajeros()
      .then((response)=>{                   
        dispatch(pCajeros(response.result))       
        dispatch(mStop())
      })
      .catch((err)=>{
        console.log(err)
      })
    };
  }

  export function pCajeros(response) {
    return {
      type: 'GET_CAJEROS',
      payload: response
    };
  }

  function resetL(xredux) {
    return (dispatch) => {         
       console.log(xredux)
        dispatch(pReset(xredux))            
    };
  }

  export function pReset(xredux) {
    return {
      type: xredux
    };
  }





  function getCategorias() {
    return (dispatch) => {            
      clientesApi
      .getCategorias()
      .then((response)=>{                   
        dispatch(pCategorias(response.result))       

      })
      .catch((err)=>{
        console.log(err)
      })
    };
  }

  export function pCategorias(response) {
    return {
      type: 'GET_CATEGORIAS',
      payload: response
    };
  }  

  function getDatas(page,num,categoriaId,estado,nombre) {
    return (dispatch) => {                  
      clientesApi
      .getData(page,num,categoriaId,estado,nombre)
      .then((response)=>{                   
        dispatch(pData(response.result))        
      })
      .catch((err)=>{
        console.log(err)
      })
    };
  }

  function getData(page,num,categoriaId,estado,nombre) {
    return (dispatch) => {            
      /*dispatch(mBegin())*/
      clientesApi
      .getData(page,num,categoriaId,estado,nombre)
      .then((response)=>{                           
        dispatch(pData(response.result))
        /*dispatch(mEnd())*/
      })
      .catch((err)=>{
        console.log(err)
      })
    };
  }
  export function pData(response) {
    return {
      type: 'GET_DATA',
      payload: response
    };
  }  


  function getItem(itemId) {
    return (dispatch) => {      
      dispatch(mBegin())      
      clientesApi
      .getItem(itemId)
      .then((response)=>{                                      
        dispatch(pItem(response))   
        dispatch(mEnd())             
      })
      .catch((err)=>{
        console.log(err)
      })
    };
  }

  export function pItem(response) {
    return {
      type: 'GET_ITEM',
      payload: response.cliente,
      payloads: response.sucursales
    };
  }  


  export function mBegin() {
    return {
      type: 'START_MODAL'
    };
  }  

  export function mEnd() {
    return {
      type: 'END_MODAL'
    };
  }    


  export function mStart() {
    return {
      type: 'START_LOAD'
    };
  }  

  export function mStop() {
    return {
      type: 'STOP_LOAD'
    };
  }
