import React from "react";
import { FontAwesome5 } from '@expo/vector-icons'
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { createStackNavigator } from '@react-navigation/stack'

import { LogoIcon, MenuIcon, PerfilIcon, BackIcon, SearchIcon } from '../components/Icons'
import HomeScreen from '../screens/Home/HomeScreen';
import CajerosScreen from '../screens/Maps/CajerosScreen'
import ServiciosScreen from '../screens/Maps/ServiciosScreen'
import ComidasScreen from '../screens/Maps/ComidasScreen'
import EmergenciasScreen from '../screens/Maps/EmergenciasScreen'
import DetailScreen from '../screens/Home/DetailScreen'
import UbicacionScreen from '../screens/Home/UbicacionScreen'
import LoginScreen from '../screens/Login/LoginScreen'
import RegisterScreen from '../screens/Register/RegisterScreen'
import PerfilScreen from '../screens/Home/PerfilScreen'
import { begguTheme } from '../constants'

const Stack = createStackNavigator();

const screenOptionStyle = {
    headerStyle: {
      backgroundColor: "#9AC4F8",
    },
    headerTintColor: "white",
    headerBackTitle: "Back",
  };

const MainStackNavigator = (props)=>{
    return(
    <Stack.Navigator 
        initialRouteName="Home" 
        screenOptions={screenOptionStyle} >
        <Stack.Screen
          name="Home"
          component={HomeScreen}
          options={{                
            headerTitle: () => <LogoIcon {...props}/>,       
            headerRight: () => <PerfilIcon {...props}/>,
            headerStyle: {
                        backgroundColor: begguTheme.COLORS.DEFAULT,
                        borderBottomColor: 'black',
                        borderBottomWidth: 0,
                      },
                }}
        />
        <Stack.Screen
          name="Detail"
          component={DetailScreen}
          options={{                                                          
            headerTitle:"Detalle",
            headerStyle: {
                backgroundColor: begguTheme.COLORS.DEFAULT,
                borderBottomColor: 'black',
                borderBottomWidth: 0,
               },
          }}
        />
        <Stack.Screen
          name="Ubicacion"
          component={UbicacionScreen}
          options={{                                                          
            headerStyle: {
            backgroundColor: begguTheme.COLORS.DEFAULT,
            borderBottomColor: 'black',
            borderBottomWidth: 0,
              },
            }}
        />
        <Stack.Screen
          name="Login"
          component={LoginScreen}
          options={{                                                          
            headerStyle: {
            backgroundColor: begguTheme.COLORS.DEFAULT,
            borderBottomColor: 'black',
            borderBottomWidth: 0,
              },
            }}
        />
        <Stack.Screen
          name="Register"
          component={RegisterScreen}
          options={{                                                          
            headerStyle: {
            backgroundColor: begguTheme.COLORS.DEFAULT,
            borderBottomColor: 'black',
            borderBottomWidth: 0,
              },
            }}
        />
        <Stack.Screen
          name="Perfil"
          component={PerfilScreen}
          options={{                                                          
            headerStyle: {
            backgroundColor: begguTheme.COLORS.DEFAULT,
            borderBottomColor: 'black',
            borderBottomWidth: 0,
              },
            }}
        />
    </Stack.Navigator>
    )
}

const CajerosStackNavigator = (props) => {
    return (
      <Stack.Navigator screenOptions={screenOptionStyle}>
        <Stack.Screen 
         options={{
          headerLeft: () => <BackIcon {...props}/>,                                               
          headerStyle: {
              backgroundColor: begguTheme.COLORS.DEFAULT,
              borderBottomColor: 'black',
              borderBottomWidth: 0,
            },
        }}
        name="Cajeros" 
        component={CajerosScreen} />
      </Stack.Navigator>
    );
  }

  const ServiciosStackNavigator = (props) => {
    return (
      <Stack.Navigator screenOptions={screenOptionStyle}>
        <Stack.Screen 
         options={{
          headerLeft: () => <BackIcon {...props}/>,                                               
          headerStyle: {
              backgroundColor: begguTheme.COLORS.DEFAULT,
              borderBottomColor: 'black',
              borderBottomWidth: 0,
            },
        }}
        name="Servicios" 
        component={ServiciosScreen} />
      </Stack.Navigator>
    );
  }

  const ComidasStackNavigator = (props) => {
    return (
      <Stack.Navigator screenOptions={screenOptionStyle}>
        <Stack.Screen 
         options={{
          headerLeft: () => <BackIcon {...props}/>,                                               
          headerStyle: {
              backgroundColor: begguTheme.COLORS.DEFAULT,
              borderBottomColor: 'black',
              borderBottomWidth: 0,
            },
        }}
        name="Comidas" 
        component={ComidasScreen} />
      </Stack.Navigator>
    )
      }

   const EmergenciasStackNavigator = (props) => {
        return (
          <Stack.Navigator screenOptions={screenOptionStyle}>
            <Stack.Screen 
                options={{
                  headerLeft: () => <BackIcon {...props}/>,                                               
                  headerStyle: {
                      backgroundColor: begguTheme.COLORS.DEFAULT,
                      borderBottomColor: 'black',
                      borderBottomWidth: 0,
                    },
                }}
                name="Emergencias" 
                component={EmergenciasScreen} 
                />
          </Stack.Navigator>
        );
    }    

const Tab = createBottomTabNavigator();

const BottomTabNavigator = () => {
  return (
    <Tab.Navigator
    screenOptions={({ route }) => ({
        tabBarIcon: ({ color, size }) => {
          const icons = {
            Home: 'home',
            Emergencias: 'notes-medical',
            Servicios:'car-crash',
            Cajeros:'credit-card',
            Comidas:'utensils'

          };

          return (
            <FontAwesome5
              name={icons[route.name]}
              color={color}
              size={size}
            />
          );
        },
      })} 
    >
      <Tab.Screen name="Home" component={MainStackNavigator} />
      <Tab.Screen name="Emergencias" component={EmergenciasStackNavigator } />
      <Tab.Screen name="Cajeros" component={CajerosStackNavigator} />
      <Tab.Screen name="Servicios" component={ServiciosStackNavigator} />
      <Tab.Screen name="Comidas" component={ComidasStackNavigator} />
    </Tab.Navigator>
  );

};

export default BottomTabNavigator;
