import React, {useState, createRef} from 'react';
import { TouchableOpacity, StyleSheet, ScrollView, Image, KeyboardAvoidingView, Keyboard } from 'react-native';
import { Block, Text, Button as GaButton, theme, Input, Icon } from "galio-framework";
import { begguTheme } from "../../constants";
import { useSelector, useDispatch } from 'react-redux'
import {clientesActions} from '../../actions'

import Loader from '../../screens/components/loader';

const RegisterScreen = ({navigation}) => {
  const [userNombre, setUserNombre] = useState('')
  const [userTelefono, setUserTelefono] = useState('')
  const [userEmail, setUserEmail] = useState('')
  const [userUsername, setUserUsername] = useState('')
  const [userPassword, setUserPassword] = useState('')
  const [userRefNombre, setRefNombre] = useState('')
  const [userRefEmail, setUserRefEmail] = useState('')  
  const [errortext, setErrortext] = useState('');
  const loading = useSelector(state => state.clientes.loading)  
  const isRegistraionSuccess = useSelector(state => state.clientes.isRegistraionSuccess)


  const dispatch = useDispatch()

  const handleSubmitButton = () => {
    setErrortext('');
    if (!userNombre) {
      alert('Por favor ingrese nombre');
      return;
    }
    if (!userTelefono) {
      alert('Por favor ingrese teléfono');
      return;
    }
    if (!userEmail) {
      alert('Por favor ingrese email');
      return;
    }
    if (!userUsername) {
      alert('Por favor ingrese username');
      return;
    }
    if (!userPassword) {
      alert('Por favor ingrese password');
      return;
    }
    if (!userRefNombre) {
      alert('Por favor ingrese nombre referencia');
      return;
    }
    if (!userRefEmail) {
      alert('Por favor ingrese mail referencia');
      return;
    }    
    var dataToSend = {
      nombre: userNombre,
      email: userEmail,
      telefono: userTelefono,
      username: userUsername,
      password: userPassword,
      refnombre: userRefNombre,
      refemail: userRefEmail      
    };
    dispatch(clientesActions.registro(dataToSend))

  } 

  if (isRegistraionSuccess) {
    return (
      <Block 
        style={{
          flex: 1,
          backgroundColor: '#eaeaea',
          justifyContent: 'center',
        }}>        
        <Text style={styles.successTextStyle}>
          Registro exitoso !!
        </Text>
        <TouchableOpacity
          style={styles.buttonStyle}
          activeOpacity={0.5}
          onPress={() => navigation.navigate('Login')}>          
          <Text style={styles.buttonTextStyle}>Inicia sesión</Text>
        </TouchableOpacity>
      </Block>
    );
  }

  return (
  <Block flex style={{backgroundColor: '#eaeaea', paddingTop: 10}}>
    <Loader loading={loading} />
    <Block flex style={styles.group}>
    <ScrollView
        keyboardShouldPersistTaps="handled"
        contentContainerStyle={{
          justifyContent: 'center',
          alignContent: 'center',
        }}>       
      <KeyboardAvoidingView enabled>
      <Block style={{ paddingHorizontal: theme.SIZES.BASE }}>
        <Text style={styles.textInput}>Datos personales</Text>
      </Block>
      <Block style={{ paddingHorizontal: theme.SIZES.BASE }}>
        <Input
            placeholder="Nombres"
            placeholderTextColor={begguTheme.COLORS.MUTED}
            style={styles.inputStyle}
            color={begguTheme.COLORS.HEADER}    
            onChangeText={(Nombre) => setUserNombre(Nombre)}
            iconContent={
              <Icon
                size={14}
                color={begguTheme.COLORS.ICON}
                name="link"
                family="AntDesign"
                style={styles.icons}
              />
            }  

        />
      </Block>
      <Block style={{ paddingHorizontal: theme.SIZES.BASE }}>
        <Input
            placeholder="Email"
            placeholderTextColor={begguTheme.COLORS.MUTED}
            style={styles.inputStyle}
            color={begguTheme.COLORS.HEADER}    
            onChangeText={(Email) => setUserEmail(Email)}
            iconContent={
              <Icon
                size={14}
                color={begguTheme.COLORS.ICON}
                name="link"
                family="AntDesign"
                style={styles.icons}
              />
            }  

        />
      </Block>
      <Block style={{ paddingHorizontal: theme.SIZES.BASE }}>
        <Input
            placeholder="Teléfono"
            placeholderTextColor={begguTheme.COLORS.MUTED}
            style={styles.inputStyle}
            color={begguTheme.COLORS.HEADER}    
            onChangeText={(Telefono) => setUserTelefono(Telefono)}
            iconContent={
              <Icon
                size={14}
                color={begguTheme.COLORS.ICON}
                name="link"
                family="AntDesign"
                style={styles.icons}
              />
            }  

        />
      </Block>  
      <Block style={{ paddingHorizontal: theme.SIZES.BASE }}>
        <Input
            placeholder="Username"
            placeholderTextColor={begguTheme.COLORS.MUTED}
            style={styles.inputStyle}
            color={begguTheme.COLORS.HEADER}    
            onChangeText={(Username) => setUserUsername(Username)}
            iconContent={
              <Icon
                size={14}
                color={begguTheme.COLORS.ICON}
                name="link"
                family="AntDesign"
                style={styles.icons}
              />
            }  

        />
      </Block>    
      <Block style={{ paddingHorizontal: theme.SIZES.BASE }}>
        <Input
            placeholder="Password"
            placeholderTextColor={begguTheme.COLORS.MUTED}
            style={styles.inputStyle}
            secureTextEntry={true}
            color={begguTheme.COLORS.HEADER}    
            onChangeText={(Password) => setUserPassword(Password)}
            iconContent={
              <Icon
                size={14}
                color={begguTheme.COLORS.ICON}
                name="link"
                family="AntDesign"
                style={styles.icons}
              />
            }  

        />
      </Block>

      <Block style={{ paddingHorizontal: theme.SIZES.BASE }}>
        <Text style={styles.textInput}>Datos de referencia</Text>
      </Block>

      <Block style={{ paddingHorizontal: theme.SIZES.BASE }}>
        <Input
            placeholder="Nombre referencia"
            placeholderTextColor={begguTheme.COLORS.MUTED}
            style={styles.inputStyle}
            color={begguTheme.COLORS.HEADER}    
            onChangeText={(RefNombre) => setRefNombre(RefNombre)}
            iconContent={
              <Icon
                size={14}
                color={begguTheme.COLORS.ICON}
                name="link"
                family="AntDesign"
                style={styles.icons}
              />
            }  

        />
      </Block>
      <Block style={{ paddingHorizontal: theme.SIZES.BASE }}>
        <Input
            placeholder="Email referencia"
            placeholderTextColor={begguTheme.COLORS.MUTED}
            style={styles.inputStyle}
            color={begguTheme.COLORS.HEADER}    
            onChangeText={(RefEmail) => setUserRefEmail(RefEmail)}
            iconContent={
              <Icon
                size={14}
                color={begguTheme.COLORS.ICON}
                name="link"
                family="AntDesign"
                style={styles.icons}
              />
            }  

        />
      </Block>
      <Block style={{ paddingHorizontal: theme.SIZES.BASE }}>
          <TouchableOpacity
            style={styles.buttonStyle}
            activeOpacity={0.5}
            onPress={handleSubmitButton}>
            <Text style={styles.buttonTextStyle}>
              REGISTER
            </Text>
          </TouchableOpacity>
      </Block>
      </KeyboardAvoidingView>  
    </ScrollView>  
    </Block>
  </Block> 
  );
};
export default RegisterScreen;

const styles = StyleSheet.create({
  sectionInput:{
    flexDirection: 'row',
    height: 40,
    marginTop: 20,
    marginLeft: 35,
    marginRight: 35,
    margin: 10,
    borderWidth: 3,
    borderColor: "#fff"
  },
  inputStyle: {
    flex:1,
    width: '100%',
    borderRadius: 4,
    borderColor: begguTheme.COLORS.BORDER,    
    backgroundColor: '#FFFFFF',
    paddingLeft: 15
  },
  icons: {    
    paddingLeft: 2,
    paddingRight: 15
  },
  success: {
    borderColor: begguTheme.COLORS.INPUT_SUCCESS,
  },
  error: {
    borderColor: begguTheme.COLORS.INPUT_ERROR,
  },
  shadow: {
    shadowColor: begguTheme.COLORS.BLACK,
    shadowOffset: { width: 0, height: 1 },
    shadowRadius: 2,
    shadowOpacity: 0.05,
    elevation: 2,
  },
  textInput:{
    color:"#4d4d4d",
    fontSize: 16,
    height: 17,
    marginBottom:1
  },
  buttonStyle: {
    backgroundColor: '#7DE24E',
    borderWidth: 0,
    color: '#FFFFFF',
    borderColor: '#7DE24E',
    height: 40,
    alignItems: 'center',
    borderRadius: 10,
    marginLeft: 35,
    marginRight: 35,    
    marginBottom: 20,
  },
  buttonTextStyle: {
    color: '#FFFFFF',
    paddingVertical: 10,
    fontSize: 16,
  },
  errorTextStyle: {
    color: 'red',
    textAlign: 'center',
    fontSize: 14,
  },
  successTextStyle: {
    color: 'red',
    textAlign: 'center',
    fontSize: 18,
    padding: 30,
  },

});
